mapper.select:
SELECT
order_id,service_id,user_id,service_count,order_status,create_time,over_time,service_name,order_price
FROM
tb_order 


		and order_status= #{orderCondition.orderStatus}


		and user_id = #{orderCondition.userId}


		and service_id = #{orderCondition.serviceId}


	    and DATE_FORMAT(create_time,'%Y %m %d') >=  DATE_FORMAT(#{orderCondition.createTime},'%Y %m %d')


	    and DATE_FORMAT(over_time,'%Y %m %d') >=  DATE_FORMAT(#{orderCondition.overTime},'%Y %m %d')


ORDER BY order_id desc
LIMIT #{rowIndex},#{pageSize};

SELECT
count(1)
FROM
tb_order t


		and order_status= #{orderCondition.orderStatus}


		and user_id = #{orderCondition.userId}


		and service_id = #{orderCondition.serviceId}


	    and DATE_FORMAT(create_time,'%Y %m %d') >=  DATE_FORMAT(#{orderCondition.createTime},'%Y %m %d')


	    and DATE_FORMAT(over_time,'%Y %m %d') >=  DATE_FORMAT(#{orderCondition.overTime},'%Y %m %d')

mapper.update:
update tb_order
service_id=#{serviceId}, user_id=#{userId}, service_count=#{serviceCount}, order_status=#{orderStatus}, create_time=#{createTime}, over_time=#{overTime}, service_name=#{serviceName}, order_price=#{orderPrice}
where order_id=#{orderId}

mapper.select:
SELECT
order_id,service_id,user_id,service_count,order_status,create_time,over_time,service_name,order_price
FROM
tb_order 


		and order_status= #{orderCondition.orderStatus}


		and user_id = #{orderCondition.userId}


		and service_id = #{orderCondition.serviceId}


	    and DATE_FORMAT(create_time,'%Y %m %d') >=  DATE_FORMAT(#{orderCondition.createTime},'%Y %m %d')


	    and DATE_FORMAT(over_time,'%Y %m %d') >=  DATE_FORMAT(#{orderCondition.overTime},'%Y %m %d')


ORDER BY order_status asc
